def _draw_logo(c, path, x, y, max_w, max_h):
    try:
        if not path or not os.path.exists(path): return
        img = ImageReader(path)
        iw, ih = img.getSize()
        ratio = min(max_w/iw, max_h/ih)
        w, h = iw*ratio, ih*ratio
        c.drawImage(img, x, y-h, width=w, height=h, mask='auto')
    except Exception:
        pass
