        try:
            center_lbl.raise_(); center_lbl.show()
        except Exception:
            pass
        # Gauche: logo entreprise (Paramètres)
        path = ""
        try:
            path = _logo_from_settings(getattr(self, "settings", {}) or {})
        except Exception:
            path = ""
        if path and os.path.exists(path):
            left_lbl = getattr(self, "_banner_left_logo", None)
