def _hex_to_rgb(hexstr:str):
    try:
        h = (hexstr or "").strip().lstrip("#")
        if len(h) == 3:
            h = "".join(ch*2 for ch in h)
        if len(h) != 6:
            return (0.0, 0.52, 1.0)  # fallback #0A84FF
        r = int(h[0:2], 16); g = int(h[2:4], 16); b = int(h[4:6], 16)
        return (r/255.0, g/255.0, b/255.0)
    except Exception:
        return (0.0, 0.52, 1.0)
