            vals=dlg.values()
            # assign ID (CLxxxx)
            rows=load_csv(os.path.join(DATA_DIR, CSV_CLIENTS))
            import re
            mx=0
            for x in rows:
                s=(x.get("ID","") or "")
                m=re.match(r"CL(\d+)$", s)
                if m:
                    mx=max(mx, int(m.group(1)))
                elif s.isdigit():
                    mx=max(mx, int(s))
            new_id=f"CL{mx+1:04d}"
            vals["ID"]=new_id
            rows.append(vals)
            save_csv(rows["ID","Genre","Nom","Prenom","Email","Telephone","Adresse","CodePostal","Ville","SIRET"])
