        ur_tot = ur_m + ur_p
    
        self.lbl_tva.setText(f"TVA ({taux_moyen:.1f} %) : {tva_amount:.2f} €    •    URSSAF MO ({int(round(rate_m*100))} %) : {ur_m:.2f} €    •    URSSAF Pièces ({int(round(rate_p*100))} %) : {ur_p:.2f} €    •    Total : {ur_tot:.2f} €")
    
        # Mise à jour des totaux
