def _sf_format_num(x):
    try:
        import math
        if x is None: return ""
        if isinstance(x, str):
            x = x.strip().replace("\xa0"," ").replace("€","").replace("%","").replace(",", ".")
            x = float(x) if x else 0.0
        if isinstance(x, (int,float)) and (math.isfinite(x)):
            return f"{x:,.2f}".replace(",", "X").replace(".", ",").replace("X", " ")
        return str(x)
    except Exception:
        try: return str(x)
        except Exception: return ""
